import { mapGetters, mapActions } from "vuex";
export default {
  name: 'Workshop_list_SA',
  components: {
    
  },
  data() {
    
    return {
      centerDialogVisible: false,
   titles: [{
    prop: "id",
    label: "#"
    }, {
    prop: "WorkShopName",
    label: "Workshop Name"
    }, {
    prop: "OwnerName",
    label: "Owner Name"
  },
  {
    prop: "Email",
    label: "Email"
  },{
    prop: "Address",
    label: "Address"
  }
  ],
      actionCol: {
        props: {
          label: 'Action',
          className: 'actionCenter'
        },
       buttons: [{
          props: {
            type: 'primary'
          },
          handler: row => {
           
            let id = row.WorkShopID
            this.$router.push({path: '/workshop_detail/' +id})
          },
          label: <img src='data:image/png;base64,' />
        }, 

         {
          handler: row => {
            let id = row.WorkShopID
            this.$router.push({path: '/workshop/' +id})
          },
          label: <img src='data:image/png;base64,'/>
        },
        {
          handler: row => {
            this.centerDialogVisible = true;
             this.corp_id=row.WorkShopID
          },
          label: <img src='data:image/png;base64,'/>
        }]
      },
    }
  },

    methods: {
    deleteWorkshop(){
 
			var self = this;
      let corpData={url:'/SuperAdmin/DeleteWorkshop?WorkShopID='+self.corp_id}
			return this.$store.dispatch('updateData', corpData).then((response) => {
        this.$store.dispatch("getWorkshopLists");
			}).catch(function(err) {
				self.$message(err.response.data.error)
			});
		
    }
  
  },

 computed: {
     
    ...mapGetters({
      data: "getWorkshopLists"
  
    })

  },
	watch: {
		'getWorkshopLists': {
			deep: true,
			handler(value) {
				this.data = null
				this.data = this.getWorkshopLists;
        
			}
		}
	},
    created() {

      this.$store.dispatch("getWorkshopLists");
      
    }

};
</script>

